bool   is_prime ( unsigned   long   long   n )   { 
     if   ( n     2 ) 
         return   false ; 
     if   ( n   ==   2 ) 
         return   true ; 
     if   ( n   %   2   ==   0 ) 
         return   false ;  // can you use an algorithm here as well? 
     for   ( int   i   =   3 ;   ( i   *   i )   =   n ;   i   +=   2 )   { 
         if   ( n   %   i   ==   0 ) 
             return   false ; 
     } 
     return   true ;  }

